CREATE TABLE `webtech`.`users` ( `username` VARCHAR(32) NOT NULL , `password` VARCHAR(128) NOT NULL , `email` VARCHAR(128) NOT NULL , `title` VARCHAR(16) NOT NULL , `firstname` VARCHAR(32) NOT NULL , `lastname` VARCHAR(32) NOT NULL , `address` VARCHAR(64) NOT NULL , `location` VARCHAR(64) NOT NULL , `plz` INT(4) NOT NULL , `is_admin` BOOLEAN NOT NULL , `is_active` BOOLEAN NOT NULL , PRIMARY KEY (`username`)) ENGINE = InnoDB;
CREATE TABLE `webtech`.`shopping-cart` ( `w_username` VARCHAR(32) NOT NULL , `w_pid` VARCHAR(32) NOT NULL , PRIMARY KEY (`w_username`)) ENGINE = InnoDB;
CREATE TABLE `webtech`.`products` ( `pid` VARCHAR(32) NOT NULL , `pr_owner` VARCHAR(64) NOT NULL , `pr_filename` VARCHAR(128) NOT NULL , `pr_exif` VARCHAR(128) NOT NULL , `pr_upload_date` TIMESTAMP(6) NOT NULL DEFAULT CURRENT_TIMESTAMP(6) , `pr_quality` VARCHAR(32) NOT NULL , `pr_creation_date` TIMESTAMP(6) NOT NULL DEFAULT CURRENT_TIMESTAMP(6) , PRIMARY KEY (`pid`)) ENGINE = InnoDB;
CREATE TABLE `webtech`.`product-tags` ( `pt_id` VARCHAR(32) NOT NULL , `t_pid` VARCHAR(32) NOT NULL , PRIMARY KEY (`pt_id`)) ENGINE = InnoDB;
CREATE TABLE `webtech`.`tag` ( `tid` VARCHAR(32) NOT NULL , `t_pid` VARCHAR(32) NOT NULL , `t_name` VARCHAR(64) NOT NULL , PRIMARY KEY (`tid`)) ENGINE = InnoDB;
